import React, { useRef } from "react";
import "./Bodycss.css";
import "./Formcss.css";
import "bootstrap/dist/css/bootstrap.css";
import emailjs from "@emailjs/browser";

const Form = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    const success = document.getElementById("success");
    const danger = document.getElementById("danger");
    if (
      document.getElementById("Name").value === "" ||
      document.getElementById("Email").value === "" ||
      document.getElementById("c-Name").value === ""
    ) {
      success.style.display = "none";
      danger.style.display = "block";
    } else {
      danger.style.display = "none";
      success.style.display = "block";
      emailjs
        .sendForm(
          "service_fr1q2yo",
          "template_in9sta7",
          form.current,
          "IwpSeR1C6g1SAhzah"
        )
        .then(
          (result) => {
            console.log(result.text);
          },
          (error) => {
            console.log(error.text);
          }
        );
    }
  };
  return (
    <div className="row form">
      <div className="col-md-6">
        <h4 className="text">Would like to see a demo, Reach out to us</h4>
      </div>
      <div className="col-md-6">
        <form ref={form} onSubmit={sendEmail}>
          <div className="row mb-3">
            <label for="Email" className="col-sm-2 col-form-label">
              <p>Email</p>
            </label>
            <div className="col-sm-10">
              <input
                type="email"
                name="Email"
                required="true"
                className="form-control form-text"
                id="Email"
              />
            </div>
          </div>
          <div className="row mb-3">
            <label for="Name" className="col-sm-2 col-form-label">
              <p>Name</p>
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                name="Name"
                className="form-control form-text"
                id="Name"
              />
            </div>
          </div>
          <div className="row mb-3">
            <label for="c-Name" className="col-sm-2 col-form-label">
              <p>Company Name</p>
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                name="c-Name"
                className="form-control form-text"
                id="c-Name"
              />
            </div>
          </div>
          <div id="success">
            <p>
              ✅ Thank you for submitting your details. We will reach out to you
              soon.
            </p>
          </div>
          <div id="danger">
            <p>Fields can't be empty.</p>
          </div>
          <input type="submit" className="button" value="Book a Demo" />
        </form>
      </div>
    </div>
  );
};

export default Form;
